import React, { useState } from "react";
import CheckAll from "./components/CheckAll";
import Form from "./components/Form";
import Todo from "./components/Todo";

let currentId = 0;

const App = () => {
  const [state, setState] = useState({
    todos: [],
  });
  const {todos} = state;
  // console.log(state);
  console.log(todos)

  const handleSubmit = (text) => {
    const newTodo = {
      id: currentId,
      text: text,
      completed: false,
    };
    const newTodos = [...state.todos, newTodo];
    setState({ todos: newTodos });
    currentId++;
  };

  const handleChangeAllCompleted = completed => {
    const newTodos = state.todos.map(todo => ({
      ...todo,completed
    }))
    setState({todos: newTodos})
  }

  const handleChangeCompleted = (id , completed) => {
    const newTodos = state.todos.map((todo) => {
      if (todo.id === id) {
        return {
          ...todo,
          completed,
        };
      }
      return todo;
    });
    setState({ todos: newTodos });
  };



  return (
    <div>
      <Form onSubmit={handleSubmit} />

     <CheckAll allCompleted={todos.length > 0 && todos.every(({completed}) => completed)} onChange={handleChangeAllCompleted} />

      <select>
        <option>全て</option>
        <option>未完了</option>
        <option>完了済み</option>
      </select>

      <ul>
        {state.todos.map(({ id, text, completed }) => {
          return (
            <li key={id}>
              <Todo
                id={id}
                text={text}
                completed={completed}
                onChange={handleChangeCompleted}
              />
            </li>
          );
        })}
      </ul>
      <button onClick="handleClickDeleteCompleted">完了済みを全て削除</button>
    </div>
  );
};

export default App;
