import React,{useState} from "react";
import Form from "./components/Form";
import Todo from "./components/Todo";

const App = () => {
  const [state, setState] = useState({
    todos :[
      {
        id:0,
        text:"朝ご飯を食べる"
      },
      {
        id:1,
        text:"筋トレをする"
      },
      {
        id:2,
        text:"勉強をする"
      }
    ]
  })
  console.log(state)
  return (
    <div>
      <Form />
      <label>
        <input type="checkbox"></input>
      </label>

      <select>
        <option>全て</option>
        <option>未完了</option>
        <option>完了済み</option>
      </select>

      <ul>
        {state.todos.map(({id,}))}
        <li>
          <Todo id={0} text={"朝ご飯を食べる"} />
        </li>
        <li>
          <Todo id={0} text={"筋トレをする"} />
        </li>
        <li>
          <Todo id={0} text={"勉強をする"} />
        </li>
      </ul>
      <button>完了済みを全て削除</button>
    </div>
  );
};

export default App;
