import React, { useState } from "react";
import CheckAll from "./components/CheckAll";
import EditTodo from "./components/EditTodo";
import Form from "./components/Form";
import Todo from "./components/Todo";

let currentId = 0;

const App = () => {
  const [state, setState] = useState({
    todos: [],
  });
  const { todos } = state;
  console.log(state);

  const handleSubmit = (text) => {
    const newTodo = {
      id: currentId,
      text: text,
      completed: false,
      editing: false,
    };
    const newTodos = [...state.todos, newTodo];
    setState({ todos: newTodos });
    currentId++;
  };

  const handleChangeAllCompleted = (completed) => {
    const newTodos = state.todos.map((todo) => ({
      ...todo,
      completed,
    }));
    setState({ todos: newTodos });
  };

  const handleClickDeleteCompleted = () => {
    const newTodos = state.todos.filter(({ completed }) => !completed);
    setState({ todos: newTodos });
  };

  const handleClickDelete = (id) => {
    const newTodos = state.todos.filter((todo) => todo.id !== id);
    setState({ todos: newTodos });
  };

  const handleChangeTodoAttribute = (id, key, value) => {
    const newTodos = state.todos.map((todo) => {
      if (todo.id === id) {
        return {
          ...todo,
          [key]: value,
        };
      }
      return todo;
    });
    setState({ todos: newTodos });
    console.log({ newTodos });
  };

  const handleUpdateTodoText = (id, text) => {
    const newTodo = state.todos.map((todo) => {
      if (todo.id === id) {
        return {
          ...todo,
          text: text,
          editing: false,
        };
      }
      return todo;
    });
    setState({ todos: newTodo });
  };

  return (
    <div>
      <Form onSubmit={handleSubmit} />
      <CheckAll
        allCompleted={
          todos.length > 0 && todos.eve(({ completed }) => completed)
        }
        onChange={handleChangeAllCompleted}
      />

      <select>
        <option>全て</option>
        <option>未完了</option>
        <option>完了済み</option>
      </select>

      <ul>
        {state.todos.map(({ text, id, completed, editing }) => {
          return (
            <li key={id}>
              {editing ? (
                <EditTodo
                  id={id}
                  text={text}
                  onCancel={handleChangeTodoAttribute}
                  onSubmit={handleUpdateTodoText}
                />
              ) : (
                <Todo
                  id={id}
                  text={text}
                  complete={completed}
                  onChange={handleChangeTodoAttribute}
                  onDelete={handleClickDelete}
                />
              )}
            </li>
          );
        })}
      </ul>
      <button onClick={handleClickDeleteCompleted}>完了済みを全て削除</button>
    </div>
  );
};

export default App;
